sub B @default4 @default4 B' by B.alt1;  
sub B @default4 @default4 @default4 B' by B.alt1;  
sub B @default4 @default4 @default4 @default4 B' by B.alt1;

} rotate;

lookup rotate;

} salt;[/code]

